import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

const AboutUs = () => {
  const styles = {
    body: {
      fontFamily: 'Arial, sans-serif',
      margin: 0,
      padding: 0,
      boxSizing: 'border-box',
      backgroundColor: '#f8f9fa',
    },
    heroSection: {
      backgroundImage: "url('../../Assest/HomeBack/HomepageBackgroundpic.jpg')",
      backgroundRepeat: 'no-repeat',
      backgroundPosition: 'center',
      backgroundSize: 'cover',
      color: 'white',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      textAlign: 'center',
      padding: '0 20px',
    },
    heroContent: {
      h1: {
        fontSize: '3rem',
        marginBottom: '20px',
      },
      p: {
        fontSize: '1.2rem',
        marginBottom: '30px',
      },
      button: {
        fontSize: '1rem',
        padding: '10px 20px',
      },
    },
    aboutSection: {
      padding: '60px 0',
      backgroundColor: '#ffffff',
      textAlign: 'center',
      h2: {
        fontSize: '2.5rem',
        marginBottom: '20px',
      },
      p: {
        fontSize: '1.2rem',
        color: '#555',
        lineHeight: '1.6',
        margin: '0 auto',
        maxWidth: '800px',
      },
    },
    card: {
      borderRadius: '10px',
      marginBottom: '30px',
      cardBody: {
        padding: '20px',
        title: {
          fontSize: '1.8rem',
          marginBottom: '15px',
        },
        text: {
          fontSize: '1.1rem',
          color: '#333',
          lineHeight: '1.6',
        },
      },
    },
    footerSection: {
      backgroundColor: '#343a40',
      color: 'white',
      padding: '20px 0',
      textAlign: 'center',
      p: {
        margin: '5px 0',
      },
      a: {
        color: '#f8f9fa',
        textDecoration: 'none',
        hover: {
          textDecoration: 'underline',
        },
      },
    },
  };

  return (
    <div style={styles.body}>
      {/* Hero Section */}
      <section style={styles.heroSection}>
        <div>
          <h1 style={styles.heroContent.h1}>About HDSI Foundation</h1>
          <p style={styles.heroContent.p}>
            Learn more about the mission, vision, and values that drive us to create a positive
            impact in the world.
          </p>
          <Button style={styles.heroContent.button} variant="primary" href="#mission-section">
            Explore Our Work
          </Button>
        </div>
      </section>

      {/* About Section */}
      <section id="mission-section" style={styles.aboutSection}>
        <Container>
          <Row>
            <Col md={12}>
              <Card style={styles.card}>
                <Card.Body style={styles.card.cardBody}>
                  <Card.Title style={styles.card.cardBody.title}>Who We Are</Card.Title>
                  <Card.Text style={styles.card.cardBody.text}>
                    The HDSI Foundation is a non-profit organization committed to promoting education,
                    innovation, and community welfare.
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Container>
      </section>
    </div>
  );
};

export default AboutUs;
