import React, { useState } from 'react';
import { Button, Card, Row, Col } from 'react-bootstrap';
import './ProjectPage.css';

const ProjectCard = ({ title, image, fullText }) => {
  const [expanded, setExpanded] = useState(false);

  const shortText = fullText.length > 250 ? fullText.slice(0, 250) + '...' : fullText;

  return (
    <Card className="project-card h-100 d-flex flex-column">
      <Card.Img variant="top" src={image} />
      <Card.Body className="d-flex flex-column">
        <Card.Title>{title}</Card.Title>
        <Card.Text className="flex-grow-1">
          {expanded ? fullText : shortText}
        </Card.Text>
        <Button
          variant="secondary"
          onClick={() => setExpanded(!expanded)}
          className="mt-auto"
        >
          {expanded ? 'Show Less' : 'Learn More'}
        </Button>
      </Card.Body>
    </Card>
  );
};

const ProjectPage = () => {
  const projects = [
    {
      title: 'Water Project',
      image: require('../../Assest/HomeBack/hp1.jpeg'),
      fullText: `Access to clean drinking water is a fundamental necessity, yet many rural communities struggle with water scarcity...`
    },
    {
      title: 'Education',
      image: require('../../Assest/HomeBack/hp.jpeg'),
      fullText: `The Human Development Society of India (HDSI) is dedicated to delivering quality education to underserved communities...`
    },
    {
      title: 'Livelihood',
      image: require('../../Assest/HomeBack/gs.JPG'),
      fullText: `HDSI runs various livelihood initiatives in rural Amravati to create sustainable income opportunities...`
    }
  ];

  return (
    <div>
      <section className="project-header">
        <div className="container">
          <h1>Our Projects</h1>
          <p>Explore our wide range of impactful projects that are transforming lives and communities.</p>
        </div>
      </section>

      <section className="project-gallery">
        <div className="container">
          <Row>
            {projects.map((project, idx) => (
              <Col md={4} key={idx}>
                <ProjectCard {...project} />
              </Col>
            ))}
          </Row>
          <div className="load-more-btn">
            <Button variant="primary">Load More Projects</Button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default ProjectPage;
