import React from 'react';
import './homepage.css';
import Button from 'react-bootstrap/Button';

const HomePage = () => {
  return (
    <div style={{ fontFamily: 'Poppins, sans-serif' }}>
      <section className="homepage-hero-section">
        <div className="homepage-hero-content">
          <h1>Welcome to HDSI </h1>
          <p>"Empowering Communities, Enabling Lives"</p>
          <Button variant="primary" href="#homepage-work-section">Learn More</Button>
        </div>
      </section>

      <section className="homepage-about-section">
        <div className="homepage-container">
          <h2>About HDSI</h2>
         
          <p>
            “Human Development Society of India (HDSI) is dedicated to empowering marginalized communities through sustainable development initiatives in water projects, education, vocational training, and women empowerment. With a mission to create self-reliant individuals and resilient communities, we have impacted thousands of lives across India.”
          </p>
        </div>
      </section>

      <section id="homepage-work-section" className="homepage-work-section">
        <div className="homepage-container">
          <h2>"Pathways to Progress: Areas We Work In"</h2>
          <div className="homepage-work-grid">
            <div className="homepage-work-item domain-box domain-box-1">
              <h3>Water Project</h3>
              <p>Making every drop count!
                In drought-prone Vidarbha, water scarcity is a critical challenge for communities.
                HDSI actively digs borewells and open wells, ensuring sustainable water solutions for villages.
                Our efforts aim to improve access to water for drinking, household needs, and agriculture.</p>
            </div>
            <div className="homepage-work-item domain-box domain-box-2">
              <h3>Livelihood</h3>
              <p> Empowering rural communities!
                HDSI runs various livelihood initiatives in rural Amravati to create sustainable income opportunities.
                Our projects focus on animal husbandry, small-scale product manufacturing, and agriculture.
                By equipping communities with resources and skills, we promote economic independence.</p>
            </div>
            <div className="homepage-work-item domain-box domain-box-3">
              <h3>Women Empowerment</h3>
              <p>Strength in unity!
                HDSI empowers women through the formation of Self-Help Groups (SHGs) in rural areas.
                These SHGs provide financial support, training, and a platform for women to start their ventures.
                With our guidance, many women-led startups are now thriving and inspiring change.</p>
            </div>
            <div className="homepage-work-item domain-box domain-box-4">
              <h3>Education</h3>
              <p> Transforming lives through knowledge!
                HDSI has planted three Primary English Schools in the rural and tribal parts of Amravati District.
                These schools provide access to quality education for children who previously had limited opportunities.
                We believe education is the foundation for brighter futures and sustainable community development.</p>
            </div>
            <div className="homepage-work-item domain-box domain-box-5">
              <h3>Geriatric Care </h3>
              <p>Dignity in every phase of life!
